<h1>Quản lý users</h1><?php	if(!isset($_SESSION["userType"])||$_SESSION["userType"]!=1)	die("<h2>Chức năng chỉ dành cho Quản trị viên!</h2>");?><table id="listperson" class="tablesorter">	<thead>	<tr>		<th>ID</th>		<th>Tài Khoản</th>		<th>Loại tài khoản</th>		<th>Thông tin</th>		<th>Quản lý</th>	</tr>	</thead><?php	$db = new DB();	$sql = "select * from USER WHERE UID <> ".$_SESSION["userID"]."";	$result = $db->Query($sql);	if(!$result){		echo "<div id='result'>Có lỗi xảy ra</div>";	}	else{		while($row=mysql_fetch_assoc($result)){			echo "<tr>				<td>".$row["UID"]."</td>				<td>".m_unhtmlchars($row["NAME"])."</td>				<td> <select style='' id='group-".$row["UID"]."'>";			$sql = "select * from `GROUP`";			$rs = $db->Query($sql);			if(!$rs){				echo "<option>Lỗi</option>";			}			while($tmp=mysql_fetch_assoc($rs)){				if($tmp['GID']!=$row['GID']) echo "<option value='".$tmp['GID']."'>".$tmp['NAME']."</option>";				else echo "<option value='".$tmp['GID']."' selected>".$tmp['NAME']."</option>";			}						echo "</select></td>				<td contenteditable='true'>".m_unhtmlchars($row["INFO"])."</td>";			echo"				<td><a href='javascript:deleteValue({name:\"user\",id:".$row['UID']."})'>Xoá</a></td>			</tr>";		}	}?></table><a href="javascript:showAddDonate()"><input class='sendTITLE' style="width: 150px; margin-left: 20px;" type='button' value='Thêm mới user'/></a><input class='sendTITLE' type='button' value='Cập nhật thay đổi' style="width: 150px; margin-left: 20px;" onclick="editUser();"/><div id="result"></div><div id="adddonate" style="display: none;">	<div style="margin: 20px auto; width: 95%; background: rgba(255,255,255,0.5); padding: 10px; text-align: center; border-radius: 10px;">	<input id="user" type="text" style="width: 20%; margin:5px;" placeholder="Tài khoản"/>	<input id="pass" type="password" style="width: 20%; margin:5px;"  placeholder="Mật khẩu"/>	<select id="group">		<?php			$sql = "select * from `GROUP`";			$rs = $db->Query($sql);			if(!$rs){				echo "<option>Lỗi</option>";			}			while($tmp=mysql_fetch_assoc($rs)){				if($tmp['GID']!=2) echo "<option value='".$tmp['GID']."'>".$tmp['NAME']."</option>";				else echo "<option value='".$tmp['GID']."' selected>".$tmp['NAME']."</option>";			}		?>	</select>	<input id="info" type="text" style="width: 25%; margin:5px;"  placeholder="Thông tin"/>	</div>	<input class='sendTITLE' type='button' value='Thêm' onclick="addUser();"/></div>