 <?php	function uploadImgs($links){		$urls = explode("\n",$links);		echo "			<h1>Tải lên hình ảnh</h1>			<div class='save-toDB'>		";			$db = new DB();			$sql = "select * from ALBUM";			$result = $db->Query($sql);			$rows = array();			while($tmp = mysql_fetch_assoc($result)){				array_push($rows,$tmp);			}			if(count($rows)>0){								echo "Tên album:<select class='select_album' style='display:inline-block'>";					for($i=0;$i<count($rows);$i++){						echo "<option value='".$rows[$i]['ID']."' >".$rows[$i]['NAME']."</option>";					}				echo "</select>";				echo "<div class='input_album' style='display:none;'>";					echo "<input type='text' class='album_name' placeholder='Tên album'/>";				echo "</div>";				echo "<a class='switch_albummode' href='javascript:switchAlbumMode()'>Tạo album</a>";			} else {				echo "					Tên album: <input type='text' class='album_name' placeholder='Tên album' style='display:inline-block;' />					<select class='select_album' style='display:none'></select>				";			}					echo "</div>";						echo "<div id='confirm-image'>";		for($i=0;$i<count($urls);$i++){			if($urls[$i]){				echo "					<div class='image-info'>						<div class='wrapp'>							<img src='".$urls[$i]."' title='".$urls[$i]."' class='main-img'/>							<input type='text' class='img-title' placeholder='Tiêu đề' value=''>						</div>						<div class='operations'>							<div class='delete' title='Xóa khỏi danh sách' onclick='removeItem($(this))'>Xóa</div>						</div>					</div>				";			}		}		echo "</div>";		echo "<br /><input type='button' class='saveImage' value='Lưu' onclick='javascript:saveImageToDB()'/>";	}	function uploadVideos($links){		$urls = explode("\n",$links);		echo "			<h1>Tải lên video</h1>			<div class='save-toDB'>		";			$db = new DB();			$sql = "select * from ALBUM";			$result = $db->Query($sql);			$rows = array();			while($tmp = mysql_fetch_assoc($result)){				array_push($rows,$tmp);			}			if(count($rows)>0){								echo "Tên album:<select class='select_album' style='display:inline-block'>";					for($i=0;$i<count($rows);$i++){						echo "<option value='".$rows[$i]['ID']."' >".$rows[$i]['NAME']."</option>";					}				echo "</select>";				echo "<div class='input_album' style='display:none;'>";					echo "<input type='text' class='album_name' placeholder='Tên album'/>";				echo "</div>";				echo "<a class='switch_albummode' href='javascript:switchAlbumMode()'>Tạo album</a>";			} else {				echo "					Tên album: <input type='text' class='album_name' placeholder='Tên album' style='display:inline-block;' />					<select class='select_album' style='display:none'></select>				";			}					echo "</div>";						echo "<div id='confirm-image'>";		for($i=0;$i<count($urls);$i++){			if($urls[$i]){				echo "					<div class='image-info'>						<div class='wrapp'>							<img src='".youtube_poster($urls[$i])."' title='".$urls[$i]."' class='main-img'/>							<input type='text' class='img-title' placeholder='Tiêu đề' value=''>						</div>						<div class='operations'>							<div class='delete' title='Xóa khỏi danh sách'>Xóa</div>						</div>					</div>				";			}		}		echo "</div>";		echo "<br /><input type='button' class='saveImage' value='Lưu' onclick='javascript:saveVideoToDB()'/>";	} ?> <?php	if($_POST){		if(isset($_POST['image_submit'])&&$_POST['links']){			uploadImgs($_POST['links']);		} else if(isset($_POST['video_submit'])&&$_POST['links']){			uploadVideos($_POST['links']);		} else {			echo "				<h1>Lỗi</h1>				<div id='result'>Không có gì để tải lên</div>			";		}	} else {		echo "			<h1>Tải lên hình ảnh</h1>			<div id='upImage'>				<h3>Dán link ảnh vào dưới đây:</h3>				<form action='' method='POST' class='upload_form' name='imageupload_form'>					<textarea class='input-link' name='links' placeholder='Mỗi link trên 1 dòng !'></textarea>					<input type='submit' name='image_submit' class='submit-upload' value='Tải lên'></input>				</form>			</div>			<div id='result'></div>			<h1>Tải lên video</h1>			<div id='upVideo'>				<h3>Dán link video youtube vào dưới đây:</h3>				<form action='' method='POST' class='upload_form' name='videoupload_form'>					<textarea class='input-link' name='links' placeholder='Mỗi link trên 1 dòng !'></textarea>					<input type='submit' name='video_submit'class='submit-upload' value='Tải lên'></input>				</form>			</div>		";	}?>