<?php	session_start();	if(!isset($_SESSION["userType"])||$_SESSION["userType"]!=1)	die("Hackingout...");	include("../../config/config.php");	$name = m_htmlchars($_POST['name']);	$pass = md5($_POST['pass']);	$gid = $_POST['gid'];	$info = m_htmlchars($_REQUEST['info']);		$tmp = array();	$db = new DB();	$sql = "insert into USER (NAME,PASS,INFO,GID) values ('".$name."', '".$pass."', '".$info."','".$gid."')";		if($name==''||$pass==''){		$tmp['status'] = false;		$tmp['msg'] = "Chưa nhập thông tin";	}	else if(mysql_num_rows($db->Query("select * from user where NAME = '".$name."'"))>0){		$tmp['status'] = false;		$tmp['msg'] = "Tài khoản đã tồn tại";	} else{		$result = $db->Query($sql);		if(!$result){		$tmp['status'] = false;		$tmp['msg'] = "Error";		}		else{			if(!$result){				$tmp['status'] = false;				$tmp['msg'] = "Lỗi xảy ra";			}			else{				$tmp['status'] = true;				$tmp['msg'] = "Thành công";			}		}	}	echo json_encode($tmp);?>