name){				$sql = "update ALBUM set NAME='".$name."',INFO='".$info."' where ID='".$id."'";				$result = $db->Query($sql);				$fresult = array(					"status" => true,					"msg" => "Thành công",					"data" => $result,				);			} else {				$fresult = array(					"status" => false,					"msg" => "Missing parameter",					"debug" => "id,name(required)",				);			}		} else {			$fresult = array(					"status" => false,					"msg" => "Control not support",			);		}		echo json_encode($fresult);	}?>