<?php	session_start();	if(!isset($_SESSION["userType"])||$_SESSION["userType"]!=1)	die("Hackingout...");	include("../../config/config.php");	$id = $_POST['id'];	$name = m_htmlchars($_POST['name']);	$gid = $_POST['gid'];	$info = m_htmlchars($_POST['info']);		$tmp = array();	$db = new DB();	$sql = "update USER SET NAME='".$name."', INFO='".$info."', GID='".$gid."' where UID='".$id."'";		$result = $db->Query($sql);		if(!$result){			$tmp['status'] = false;			$tmp['msg'] = "Lỗi xảy ra";		}		else{			$tmp['status'] = true;			$tmp['msg'] = "Thành công";		}		echo json_encode($tmp);?>